import { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

export default function FAQAccordion() {
  const [openIndex, setOpenIndex] = useState<number | null>(0);

  const faqs = [
    {
      question: "Cunto tiempo toma ver resultados?",
      answer: "Los primeros resultados se ven en 30-45 das, pero la transformacin completa toma 3-6 meses dependiendo del punto de partida y objetivos."
    },
    {
      question: "Trabajan con negocios de cualquier tamao?",
      answer: "Nos especializamos en emprendedores, marcas emergentes y pequeas empresas que estn listos para estructurar y escalar su negocio."
    },
    {
      question: "Qu incluye el diagnstico gratuito?",
      answer: "Anlisis completo de tu presencia digital actual, identificacin de oportunidades y plan estratgico personalizado de 90 minutos."
    },
    {
      question: "Cmo funciona la automatizacin con IA?",
      answer: "Implementamos chatbots inteligentes, automatizacin de email marketing, anlisis de datos en tiempo real y optimizacin automtica de campaas."
    },
    {
      question: "Ofrecen garantas de resultados?",
      answer: "Garantizamos que seguiremos trabajando hasta que veas resultados tangibles. Si no mejora tu ROI en 6 meses, seguimos sin costo adicional."
    }
