    box-shadow: 0 0 0 2px rgba(168, 85, 247, 0.2) !important;
  }

  /* Select component placeholder styling */
  [data-placeholder] {
    color: rgba(255, 255, 255, 0.6) !important;
  }
}

@layer utilities {
  .animate-float {
    animation: float 6s ease-in-out infinite;
