  .animate-pulse-slow {
    animation: pulse 3s ease-in-out infinite;
  }

  @keyframes float {
    0%, 100% { 
      transform: translateY(0px); 
    }
    50% { 
      transform: translateY(-20px); 
    }
  }
}

